import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ProductForm from '../components/ProductForm';
import ProductList from '../components/ProductList';


function Main() {
  const [products, setProducts] = useState([]);
  const [update, setUpdate] = useState(false);

  // // Function to fetch products from the server
  // const fetchProducts = () => {
  //   axios.get('http://localhost:8000/api/products')
  //     .then(res => setProducts(res.data))
  //     .catch(err => console.log(err));
  // };

  // Function to add a new product
  const addProduct = (product) => {
    axios.post('http://localhost:8000/api/products', product)
      .then(res => {
        // Toggle the update state to force a re-render
        setUpdate(prevUpdate => !prevUpdate);
      })
      .catch(err => console.log(err));
  };

  const deleteProduct = (id) => {
    axios.delete(`http://localhost:8000/api/products/${id}`)
      .then(res => {
        // Toggle the update state to force a re-render
        setUpdate(prevUpdate => !prevUpdate);
      })
      .catch(err => console.log(err));
  };

  // Fetch products when the component mounts and whenever the update state changes
  useEffect(() => {
    const fetchProducts = () => {
      axios.get('http://localhost:8000/api/products')
        .then(res => setProducts(res.data))
        .catch(err => console.log(err));
    };

    fetchProducts();
}, [update]);


  return (
    <div>
      <ProductForm addProduct={addProduct} update={update} setUpdate={setUpdate}/>
      <ProductList products={products} deleteProduct={deleteProduct} update={update} setUpdate={setUpdate} />
    </div>
  );
}

export default Main;
